// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

contract Voting {
    // Structure to represent a voter
    struct Voter {
        uint256 weight; // Voting weight (assigned by chairperson)
        bool voted; // Whether the voter has already voted
        address delegate; // Address to whom the vote is delegated
        uint256 vote; // Index of the voted proposal
    }

    // Structure to represent a proposal
    struct Proposal {
        bytes32 name; // Name of the proposal
        uint256 voteCount; // Total votes received
    }

    address public chairperson; // The contract owner who initializes the voting process
    mapping(address => Voter) public voters; // Stores voter details by address
    Proposal[] public proposals; // List of proposals
    uint256 private winningProposalIndex; // Tracks the proposal with the highest votes

    event Voted(address indexed voter, uint256 proposal); // Event emitted when a vote is cast

    // Constructor initializes proposals and assigns chairperson role
    constructor(bytes32[] memory proposalNames) {
        chairperson = msg.sender;
        voters[chairperson].weight = 1;

        for (uint256 i = 0; i < proposalNames.length; i++) {
            proposals.push(Proposal({name: proposalNames[i], voteCount: 0}));
        }
    }

    // Grants voting rights to an address (only chairperson can call this)
    function giveRightToVote(address voter) external {
        require(msg.sender == chairperson, "Only chairperson can give right to vote.");
        require(!voters[voter].voted, "The voter has already voted.");
        require(voters[voter].weight == 0, "Voter already has voting rights.");
        voters[voter].weight = 1;
    }

    // Delegates vote to another voter
    function delegate(address to) external {
        Voter storage sender = voters[msg.sender];
        require(sender.weight != 0, "You have no right to vote");
        require(!sender.voted, "You already voted.");
        require(to != msg.sender, "Self-delegation is disallowed.");

        while (voters[to].delegate != address(0)) {
            to = voters[to].delegate;
            require(to != msg.sender, "Found loop in delegation.");
        }

        Voter storage delegate_ = voters[to];
        require(delegate_.weight >= 1, "Delegate has no voting rights.");

        sender.voted = true;
        sender.delegate = to;

        if (delegate_.voted) {
            proposals[delegate_.vote].voteCount += sender.weight;
        } else {
            delegate_.weight += sender.weight;
        }
    }

    // Allows a voter to cast their vote
    function vote(uint256 proposal) external {
        Voter storage sender = voters[msg.sender];
        require(sender.weight != 0, "Has no right to vote");
        require(!sender.voted, "Already voted.");
        require(proposal < proposals.length, "Invalid proposal index");

        sender.voted = true;
        sender.vote = proposal;
        proposals[proposal].voteCount += sender.weight;

        emit Voted(msg.sender, proposal);

        if (proposals[proposal].voteCount > proposals[winningProposalIndex].voteCount) {
            winningProposalIndex = proposal;
        }
    }

    // Returns the index of the winning proposal
    function winningProposal() public view returns (uint256) {
        return winningProposalIndex;
    }

    // Returns the name of the winning proposal
    function winnerName() external view returns (bytes32) {
        return proposals[winningProposal()].name;
    }
}
